export default class Word{
    static capitalize=s=>{try{if(typeof s==='string'){return s.split(' ').map(c=>c.charAt(0).toUpperCase()+c.slice(1)).join(' ')}else if(Array.isArray(s)){for(let i=0;i<s.length;i++){if(typeof s[i]==='string'){let p=s[i].charAt(0).toUpperCase()+s[i].slice(1);s.splice(i,1,p)}};return s}else return console.error('capilatize must contain a string or array')}catch(e){console.error(e)}}
}